        #progress {
            font-size: 30px
        }

        #loader {
            --filter: drop-shadow(0px 0px 2px #fff) drop-shadow(0px 0px 3px #00e5ff) blur(0px);
            --filter2: drop-shadow(0px 0px 25px #fff) drop-shadow(0px 0px 30px #00e5ff) blur(6px);
            --t1: scale(1) skew(0deg);
            --t2: scale(2) skew(10deg);
            --t3: scale(2) skew(-10deg);
            display: flex;
            justify-content: center;
            align-items: center;
            flex-direction: column;
            gap: 20px;
            filter: var(--filter)
        }

        .progress-bar-container {
            width: 100%;
            height: 10px;
            background: rgba(255, 255, 255, 0.2);
            border-radius: 5px;
            overflow: hidden;
        }

        .progress-bar {
            height: 100%;
            width: 0%;
            background: linear-gradient(90deg, #00e5ff, #0072ff);
            transition: width 0.3s ease-in-out;
        }

        .active {
            animation: scaleUp .09s ease-in-out
        }

        @keyframes scaleUp {
            0% {
                transform: var(--t1);
                filter: var(--filter)
            }
            50% {
                transform: var(--t2);
                filter: var(--filter2)
            }
            60% {
                transform: var(--t3)
            }
            100% {
                transform: var(--t1);
                filter: var(--filter)
            }
        }

        .completed {
            animation: completedAnimation 1s ease-in-out forwards;
        }

        @keyframes completedAnimation {
            0% {
                transform: scale(1);
                filter: var(--filter);
            }
            50% {
                transform: scale(1.2);
                filter: var(--filter2);
            }
            100% {
