    console.error('文件读取失败:', err);
    return;
  }

  // 查找第一个匹配的 # 🕛时间戳 标识的位置
  const timestampMarker = '# 🕛时间戳';
  const timestampIndex = fileContent.indexOf(timestampMarker);

  if (timestampIndex === -1) {
    console.log('未找到匹配的 # 🕛时间戳 标识');
    return;
  }

  // 找到匹配的 # 🕛时间戳 标识后的第一个换行符
  const firstLineBreakIndex = fileContent.indexOf('\n', timestampIndex);

  if (firstLineBreakIndex === -1) {
    console.log('未找到匹配的 # 🕛时间戳 标识后的换行符');
    return;
  }

  // 查找 ∞∞∞markdown-a 标识的位置
