;(async () => {
  // 获取选择的文字
  const clipboardText = quickcommand.enterData.payload;

  // 解析睡眠时间
  const sleepTimes = clipboardText.match(/(\d{2}:\d{2})-(\d{2}:\d{2})/g);

  if (sleepTimes) {
    let totalMinutes = 0;

    sleepTimes.forEach(timeRange => {
      const [start, end] = timeRange.match(/\d{2}:\d{2}/g);
      const [startHour, startMinute] = start.split(':').map(Number);
      const [endHour, endMinute] = end.split(':').map(Number);

      let startTotalMinutes = startHour * 60 + startMinute;
      let endTotalMinutes = endHour * 60 + endMinute;

      // 处理跨天的情况
      if (endTotalMinutes < startTotalMinutes) {
        endTotalMinutes += 24 * 60;
      }

      const durationMinutes = endTotalMinutes - startTotalMinutes;
      totalMinutes += durationMinutes;
    });

    const totalHours = Math.floor(totalMinutes / 60);
    const totalMinutesRemainder = totalMinutes % 60;

    // 生成新的文本
    const newText = `${clipboardText} -> ${totalHours}小时${totalMinutesRemainder > 0 ? ` ${totalMinutesRemainder}分钟` : ''}`;

    // 复制新的文本到剪切板
