            return;
        }

        console.log(`触发第 ${index + 1} 个直播间播放...`);

        // 触发鼠标进入事件
        const mouseOverEvent = new MouseEvent('mouseover', {
            bubbles: true,
            cancelable: true,
            view: window
        });

        const mouseEnterEvent = new MouseEvent('mouseenter', {
            bubbles: true,
            cancelable: true,
            view: window
        });

        // 触发事件
        targetElement.dispatchEvent(mouseOverEvent);
        targetElement.dispatchEvent(mouseEnterEvent);

        playingCount++;
        console.log(`第 ${index + 1} 个直播间已触发 (${playingCount}/${totalCount})`);
    }

    // 开始执行
    startPlayingAllCards();

})();
