// ==UserScript==
// @name         即刻动态时间详细化
// @namespace    http://tampermonkey.net/
// @version      2024-10-24
// @description  将页面中的时间格式化为 YYYY-MM-DD HH:MM
// @author       You
// @match        https://web.okjike.com/*
// @icon         https://www.google.com/s2/favicons?sz=64&domain=okjike.com
// @grant        none
// ==/UserScript==

(function() {
    'use strict';

    // 立即执行一次替换操作
    replaceTimeElements();

    // 创建一个 MutationObserver 实例
    const observer = new MutationObserver((mutationsList, observer) => {
        // 遍历所有的变化
        for (let mutation of mutationsList) {
            // 检查是否有新的节点被添加
            if (mutation.type === 'childList') {
                // 执行替换操作
                replaceTimeElements();
            }
        }
    });

    // 配置观察选项
    const config = { childList: true, subtree: true };

    // 开始观察整个文档
    observer.observe(document.body, config);

    // 替换时间元素的函数
    function replaceTimeElements() {
        // 查找所有包含 <time> 元素的 <div> 元素
        const divElements = document.querySelectorAll('div.text-ellipsis time');
        divElements.forEach(timeElement => {
            // 获取 datetime 属性的值
            const datetime = timeElement.getAttribute('datetime');
            // 将 datetime 转换为指定格式
            const formattedTime = formatDateTime(datetime);
            // 替换时间文本
            timeElement.textContent = formattedTime;
        });
    }

    // 格式化日期时间的函数
    function formatDateTime(datetime) {
        const date = new Date(datetime);
        const year = date.getFullYear();
        const month = String(date.getMonth() + 1).padStart(2, '0');
        const day = String(date.getDate()).padStart(2, '0');
        const hours = String(date.getHours()).padStart(2, '0');
        const minutes = String(date.getMinutes()).padStart(2, '0');
        return `${year}-${month}-${day} ${hours}:${minutes}`;
    }
})();
