// 定义一个函数来执行点击逻辑
function executeClickLogic() {
    // 找到所有具有 "cm-gutterElement" 类名的元素
    const elements = document.querySelectorAll('.cm-gutterElement');

    // 遍历每个元素并模拟点击
    elements.forEach(element => {
        // 找到包含 "title='Fold line'" 属性的 <span> 元素
        const spanElement = element.querySelector('span[title="Fold line"]');

        if (spanElement) {
            // 创建一个鼠标点击事件
            const clickEvent = new MouseEvent('click', {
                bubbles: true,
                cancelable: true,
                view: window
            });

            // 触发点击事件
            spanElement.dispatchEvent(clickEvent);
        }
    });
}

// 创建一个 MutationObserver 实例
const observer = new MutationObserver((mutationsList, observer) => {
    // 重新执行点击逻辑
    executeClickLogic();
});

// 配置观察选项
const config = { childList: true, subtree: true };

// 开始观察整个文档
observer.observe(document.body, config);

// 初始执行一次点击逻辑
executeClickLogic();

// 10秒钟后关闭 MutationObserver 的监听
setTimeout(() => {
    observer.disconnect();
}, 30000);
