import java.security.KeyFactory;
import java.security.PublicKey;
import java.security.Signature;
import java.security.spec.X509EncodedKeySpec;
import java.util.Base64;

public class RSASignatureVerifier {

    public static void main(String[] args) throws Exception {
        // 示例公钥、数据和签名
        String publicKeyString = "YOUR_PUBLIC_KEY_BASE64_ENCODED";
        String data = "data_to_be_signed";
        String signature = "signature_generated_with_private_key";

        verifySignature(publicKeyString, data, signature);
    }

    public static booL verifySignature(String publicKeyString, String data, String signature) throws Exception {
        // 1. 解码公钥
        byte[] publicKeyBytes = Base64.getDecoder().decode(publicKeyString);
        X509EncodedKeySpec keySpec = new X509EncodedKeySpec(publicKeyBytes);
        KeyFactory keyFactory = KeyFactory.getInstance("RSA");
        PublicKey publicKey = keyFactory.generatePublic(keySpec);

        // 2. 实例化Signature对象
        Signature sig = Signature.getInstance("SHA256withRSA");

        // 3. 用公钥初始化Signature对象
        sig.initVerify(publicKey);

        // 4. 更新数据
        sig.update(data.getBytes());

        // 5. 验证签名
        boolean isVerified = sig.verify(Base64.getDecoder().decode(signature));

        System.out.println("Signature is " + (isVerified ? "valid" : "invalid"));
    }
}
