            for (let y = 0; y < height; y++) {
                intensityLUT[y] = [];
                rgbLUT[y] = [];
                for (let x = 0; x < width; x++) {
                    const idx = (y * width + x) * 4,
                          r = pixData[idx],
                          g = pixData[idx + 1],
                          b = pixData[idx + 2],
