      function updateCalendar() {
        const now = new Date();
        const year = now.getFullYear();
        const month = now.getMonth();
        const date = now.getDate();
        const week = now.getDay();

        const weekDays = ["ä¸€", "äºŒ", "ä¸‰", "å››", "äº”", "å…­", "æ—¥"];
        const weekStr = [
          "å‘¨ä¸€",
          "å‘¨äºŒ",
          "å‘¨ä¸‰",
          "å‘¨å››",
          "å‘¨äº”",
          "å‘¨å…­",
          "å‘¨æ—¥",
        ][(week + 6) % 7];
        const monthStr = [
          "1æœˆ",
          "2æœˆ",
          "3æœˆ",
          "4æœˆ",
          "5æœˆ",
          "6æœˆ",
          "7æœˆ",
          "8æœˆ",
          "9æœˆ",
          "10æœˆ",
          "11æœˆ",
          "12æœˆ",
        ][month];

        const asideTime = new Date(`${year}/01/01 00:00:00`);
        const asideDay = Math.floor((now - asideTime) / (1000 * 60 * 60 * 24));
        const weekNum = Math.ceil(
          (asideDay + ((new Date(year, 0, 1).getDay() + 6) % 7)) / 7
        );

        const lunarDate = chineseLunar.solarToLunar(
          new Date(year, month, date)
        );
        const animalYear = chineseLunar.format(lunarDate, "A");
        const ganzhiYear = chineseLunar.format(lunarDate, "T").slice(0, -1);
        const lunarMon = chineseLunar.format(lunarDate, "M");
        const lunarDay = chineseLunar.format(lunarDate, "d");

        document.getElementById(
          "calendar-week"
        ).textContent = `ç¬¬${weekNum}å‘¨ ${weekStr}`;
        document.getElementById("calendar-date").textContent = date
          .toString()
          .padStart(2, "0");
        document.getElementById(
          "calendar-solar"
        ).textContent = `${year}å¹´${monthStr} ç¬¬${asideDay + 1}å¤©`;
        document.getElementById(
          "calendar-lunar"
        ).textContent = `${ganzhiYear}${animalYear}å¹´ ${lunarMon}${lunarDay}`;

        const calendarMain = document.getElementById("calendar-main");
        calendarMain.innerHTML = "";
        weekDays.forEach((day) => {
          const dayElement = document.createElement("div");
          dayElement.textContent = day;
          calendarMain.appendChild(dayElement);
        });

        const firstDay = new Date(year, month, 1).getDay();
        const daysInMonth = new Date(year, month + 1, 0).getDate();
        for (let i = 0; i < 42; i++) {
          const dayElement = document.createElement("div");
          const day = i - ((firstDay + 6) % 7) + 1;
          if (day > 0 && day <= daysInMonth) {
            dayElement.textContent = day;
            if (day === date) {
              dayElement.classList.add("now");
            }
          }
          calendarMain.appendChild(dayElement);
        }
      }

      function updateProgress() {
        const now = new Date();
        const year = now.getFullYear();
        const month = now.getMonth();
        const date = now.getDate();
        const week = now.getDay();

        const newYearDate = new Date("2025/01/28 00:00:00");
        const daysUntilNewYear = Math.floor(
          (newYearDate - now) / (1000 * 60 * 60 * 24)
        );
        document.getElementById("schedule-days").textContent = daysUntilNewYear;

        const yearProgress = (
          ((now - new Date(year, 0, 1)) /
            (new Date(year + 1, 0, 1) - new Date(year, 0, 1))) *
          100
        ).toFixed(1);
        const monthProgress = (
          (date / new Date(year, month + 1, 0).getDate()) *
          100
        ).toFixed(1);
        const weekProgress = (((week === 0 ? 7 : week) / 7) * 100).toFixed(1);

        document.getElementById(
          "year-progress"
