import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.jdbc.core.JdbcTemplate;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

import java.util.ArrayList;
import java.util.List;
import java.util.UUID;

@RestController
@RequestMapping("/item-unique-code")
public class MdItemUniqueCodeController {

    @Autowired
    private JdbcTemplate jdbcTemplate;

    @PostMapping("/generate")
    public void generateUniqueCodes(@RequestBody UniqueCodeRequest request) {
        String recptCode = request.getRecptCode();
        String itemCode = request.getItemCode();
        int quantityReceived = request.getQuantityReceived();

        List<Object[]> batchArgs = new ArrayList<>();
        for (int i = 1; i <= quantityReceived; i++) {
            // ç”Ÿæˆå”¯ä¸€çš„å”¯ä¸€ç 
            String uniqueCode = "UC-" + UUID.randomUUID().toString();
            batchArgs.add(new Object[]{recptCode, itemCode, 1, uniqueCode});
        }

        jdbcTemplate.batchUpdate(
            "INSERT INTO md_item_unique_code (recptCode, itemCode, quantityReceived, uniqueCode) VALUES (?, ?, ?, ?)",
            batchArgs
        );
    }

    public static class UniqueCodeRequest {
        private String recptCode;
        private String itemCode;
        private int quantityReceived;

        // Getters and Setters
        public String getRecptCode() {
            return recptCode;
        }

        public void setRecptCode(String recptCode) {
            this.recptCode = recptCode;
        }

        public String getItemCode() {
            return itemCode;
        }

        public void setItemCode(String itemCode) {
            this.itemCode = itemCode;
        }

        public int getQuantityReceived() {
            return quantityReceived;
        }

        public void setQuantityReceived(int quantityReceived) {
            this.quantityReceived = quantityReceived;
        }
    }
}
