    // é€€å‡º
    utools.hideMainWindow();

    // æ¨¡æ‹Ÿç²˜è´´æ“ä½œï¼ˆCtrl + Vï¼‰
    utools.simulateKeyboardTap('v', 'ctrl');

  } else {
    utools.showNotification('æœªæ‰¾åˆ°æœ‰æ•ˆçš„ç¡çœ æ—¶é—´æ ¼å¼');
  }
})();
